        String verb;
        String noun;
        String msg = "";

        verb = wordlist.get(0);
        noun = wordlist.get(1);
        if (!commands.contains(verb)) {
            msg = verb + " is not a known verb! ";
        }
        if (!objects.contains(noun)) {
            msg = noun + " is not a known noun! ";
        }
