    public String runCommand(String inputstr) {
        List<String> wordlist;
        String msg = "";
        String lowstr = inputstr.trim().toLowerCase();
        if (!lowstr.equals("q")) {
            if (lowstr.equals("")) {
                msg = "You must enter a command!";
            } else {
                wordlist = wordList(lowstr);
                msg = parseCommand(wordlist);
            }

        }
        return msg;
    }

